import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Handoff = props => (
  <tr>
    <td className={props.handoff.completed ? 'completed' : ''}>{props.handoff.description}</td>
    <td className={props.handoff.completed ? 'completed' : ''}>{props.handoff.responsible}</td>
    <td className={props.handoff.completed ? 'completed' : ''}>{props.handoff.priority}</td>
    <td>
      <Link to={"/edit/"+props.handoff._id}>Edit</Link>&nbsp;&nbsp;
      <Link onClick={onClickDelete} value={props.handoff._id}>Delete</Link>
    </td>
  </tr>
)

export default class HandoffList extends Component {

  constructor(props) {
    super(props);
    this.state = {handoffs: []};
  }

  componentDidMount() {
    axios.get('http://localhost:4000/handoffs/')
      .then(res => {
        this.setState({ handoffs: res.data });
      })
      .catch(function (error){
        console.log(error);
      })
  }

  onClickDelete(e) {
    e.preventDefault();

    axios.get('http://localhost:4000/handoffs/delete/'+e.target.value)
      .then(res => {
        console.log(res.data);
        let bufHandoffs = this.state.handoffs;
        debugger;
        for (let i=0; i < bufHandoffs.length; i++) {
          console.log(1);
        }


    });
  }

  handoffList() {
    return this.state.handoffs.map(function(currentHandoff, i){
      return <Handoff handoff={currentHandoff} key={i} />;
    })
  }

  render() {
    return (
      <div>
        <h3>Handoffs List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }} >
          <thead>
          <tr>
            <th>Description</th>
            <th>Responsible</th>
            <th>Priority</th>
            <th>Action</th>
          </tr>
          </thead>
          <tbody>
          { this.handoffList() }
          </tbody>
        </table>
      </div>
    )
  }
}
