import React, { Component } from 'react';
import axios from 'axios';
import { useAlert } from "react-alert";

export default class HandoffEdit extends Component {
  constructor(props) {
    super(props);

    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeResponsible = this.onChangeResponsible.bind(this);
    this.onChangePriority = this.onChangePriority.bind(this);
    this.onChangeCompleted = this.onChangeCompleted.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      id: null,
      description: '',
      responsible: '',
      priority: '',
      completed: false
    }
  }

  componentDidMount() {
    if (!this.props.match.params.id) {
      return;
    }
    axios.get('http://localhost:4000/handoffs/'+this.props.match.params.id)
      .then(response => {
        this.setState({
          id: this.props.match.params.id,
          description: response.data.description,
          responsible: response.data.responsible,
          priority: response.data.priority,
          completed: response.data.completed,
          redirectTo: null
        })
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    });
  }

  onChangeResponsible(e) {
    this.setState({
      responsible: e.target.value
    });
  }

  onChangePriority(e) {
    this.setState({
      priority: e.target.value
    });
  }

  onChangeCompleted(e) {
    this.setState({
      completed: !this.state.completed
    });
  }

  onSubmit(e) {
    e.preventDefault();

    if (this.state.id) {
      const obj = {
        description: this.state.description,
        responsible: this.state.responsible,
        priority: this.state.priority,
        completed: this.state.completed
      };
      console.log(obj);
      axios.post('http://localhost:4000/handoffs/update/'+this.props.match.params.id, obj)
        .then(res => console.log(res.data));

      // this.props.history.push('/');
      // return;
      // return <Redirect to='/'/>;
      this.setState({redirectTo: '/'});
    } else {
      const newHandoff = {
        description: this.state.description,
        responsible: this.state.responsible,
        priority: this.state.priority,
        completed: this.state.completed
      };

      axios.post('http://localhost:4000/handoffs/add', newHandoff)
        .then(res => console.log(res.data));

      this.setState({
        description: '',
        responsible: '',
        priority: '',
        completed: false
      });
    }
  }

  displayHeader() {
    if (this.state.id) {
      return <h3 align="center">Update Handoff</h3>;
    } else {
      return <h3 align="center">Create New Handoff</h3>;
    }
  }

  displayCompleted() {
    if (this.state.id) {
      return (
        <div className="form-check">
          <input  className="form-check-input"
                  id="completedCheckbox"
                  type="checkbox"
                  name="completedCheckbox"
                  onChange={this.onChangeCompleted}
                  checked={this.state.completed}
                  value={this.state.completed}
          />
          <label className="form-check-label" htmlFor="completedCheckbox">
            Completed
          </label>
        </div>
      );
    }
    return null;
  }


  render() {
    // if (this.state.redirectTo) {
    //   this.createNotification('success');
    // }

    const alert = useAlert();

    return (
      <div style={{marginTop: 10}}>
        { this.displayHeader() }
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Description: </label>
            <input  type="text"
                    className="form-control"
                    value={this.state.description}
                    onChange={this.onChangeDescription}
            />
          </div>
          <div className="form-group">
            <label>Responsible: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.responsible}
              onChange={this.onChangeResponsible}
            />
          </div>
          <div className="form-group">
            <div className="form-check form-check-inline">
              <input  className="form-check-input"
                      type="radio"
                      name="priorityOptions"
                      id="priorityLow"
                      value="Low"
                      checked={this.state.priority==='Low'}
                      onChange={this.onChangePriority}
              />
              <label className="form-check-label">Low</label>
            </div>
            <div className="form-check form-check-inline">
              <input  className="form-check-input"
                      type="radio"
                      name="priorityOptions"
                      id="priorityMedium"
                      value="Medium"
                      checked={this.state.priority==='Medium'}
                      onChange={this.onChangePriority}
              />
              <label className="form-check-label">Medium</label>
            </div>
            <div className="form-check form-check-inline">
              <input  className="form-check-input"
                      type="radio"
                      name="priorityOptions"
                      id="priorityHigh"
                      value="High"
                      checked={this.state.priority==='High'}
                      onChange={this.onChangePriority}
              />
              <label className="form-check-label">High</label>
            </div>
          </div>
          <div className="form-check">
            <input  className="form-check-input"
                    id="completedCheckbox"
                    type="checkbox"
                    name="completedCheckbox"
                    onChange={this.onChangeTodoCompleted}
                    checked={this.state.todo_completed}
                    value={this.state.todo_completed}
            />
            <label className="form-check-label" htmlFor="completedCheckbox">
              Completed
            </label>
          </div>

          <br />

          <div className="form-group">
            <input
              type="submit" value="Save" className="btn btn-primary"
              onClick={() => {alert.show("Edit saved");}}
            />
          </div>
        </form>
        </div>
    )
  }
}
