import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Button from "react-bootstrap/Button";

export default class HandoffList extends Component {

  constructor(props) {
    super(props);
    this.state = {handoffs: []};
  }

  componentDidMount() {
    axios.get('http://localhost:4000/handoffs/')
      .then(res => {
        this.setState({ handoffs: res.data });
      })
      .catch(function (error){
        console.log(error);
      })
  }

  onClickDelete = (e) => {
    e.preventDefault();

    axios.get('http://localhost:4000/handoffs/delete/'+e.target.value)
      .then(res => {
        console.log(res.data);
        let bufHandoffs = this.state.handoffs;
        debugger;
        for (let i=0; i < bufHandoffs.length; i++) {
          console.log(1);
        }
    });
  };

  displayHandoffs = (handoff, i) => {
    return (
      <tr key={i + '_' + handoff._id}>
        <td className={handoff.completed ? 'completed' : ''}>{handoff.description}</td>
        <td className={handoff.completed ? 'completed' : ''}>{handoff.responsible}</td>
        <td className={handoff.completed ? 'completed' : ''}>{handoff.priority}</td>
        <td>
          <Link to={"/edit/" + handoff._id}>Edit</Link>&nbsp;&nbsp;
          <img src={"./images/delete.png"} alt={"delete"} onClick={this.onClickDelete} value={handoff._id}>Delete</img>
        </td>
      </tr>
    );
  };


  handoffList() {
    return this.state.handoffs.map((currentHandoff, i) => {
      return this.displayHandoffs(currentHandoff, i);
    })
  }

  render() {
    return (
      <div>
        <h3>Handoffs List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }} >
          <thead>
          <tr>
            <th>Description</th>
            <th>Responsible</th>
            <th>Priority</th>
            <th>Action</th>
          </tr>
          </thead>
          <tbody>
          { this.handoffList() }
          </tbody>
        </table>
      </div>
    )
  }
}
